* {
    margin: 0;
    padding: 0;
}

body {
    box-sizing: border-box;
    font-family: Helvetica, Arial, sans-serif;
    margin:60px 40px 10px 40px;
    background-color: #eee;
    overscroll-behavior: contain;
}

/* Style buttons */
.btn {
    position: absolute;
    top: 250px;
    left: -10px;
    background-color: #ddd; /* Blue background */
    border: none; /* Remove borders */
    color: royalblue; /* White text */
    padding: 8px 10px; /* Some padding */
    font-size: 20px; /* Set a font size */
    cursor: pointer; /* Mouse pointer on hover */
}

/* Darker background on mouse-over */
.btn:hover {
    background-color: #3b5998;
    color: white;
}

#wrapper {
    max-width: 768px;
    height: 500px;
    margin: 20px auto 0;
    padding: 20px;
    overflow-y: auto;
}

@media screen and (max-width: 330px) {
    #wrapper {
        height: 400px;
    }
}



/* game grid */
.game {
    position: relative;
    width: 90%;
    height: 100%;
    /*margin: 0 auto;*/
    margin-left: 20px;
    letter-spacing: 1.5px;
    display: grid;
    grid-gap: 10px;
    grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
}

@media screen and (max-width: 330px) {
    .game {
        grid-template-columns: repeat(auto-fill, minmax(180px, 1fr));
        font-size: 0.9em;
        text-align: center;
    }
}

.box {
    background-color: #444;
    line-height: 1.4;
    height: 30px;
    color: #fff;
    border-radius: 5px;
    padding: 20px;
    font-size: 150%;
    text-align: center;
    transition: all 0.2s ease-in-out;
}


/* start button */
.start {
    position: fixed;
    top: 100px;
    background-color: #008CBA; /* blue */
    border: none;
    color: white;
    padding: 15px 15px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    font-size: 16px;
}
.start:hover {
    background-color: #4CAF50; /* green */
}

/* mode */
.mode {
    position: fixed;
    left: 50px;
    right: 50px;
    display: flex;
    justify-content: space-between;
    z-index: 10;
}

.mode > a {
    font-size: 1.5em;
    text-decoration: none;
    display: inline-block;
    padding: 8px 16px;
    background: rgba(70, 112, 105, 0.6);
    color: white;
    border-radius: 5px;
}


/* index */
.index {
    display: flex;
    position: fixed;
    justify-content: space-between;
    top: 70%;
    left: 20px;
    right: 20px;
}

@media screen and (min-width: 880px) {
    .index .previous {
        margin-left: calc(50% - 400px);
    }
    .index .next {
        margin-right: calc(50% - 370px);
    }
}

/*@media (max-width: 796px) {*/
/*    .index {*/
/*        position: fixed;*/
/*    }*/
/*}*/

.index > a {
    font-size: 1.5em;
    height: 100%;
    line-height: 100%;
    text-decoration: none;
    display: inline-block;
    padding: 4px 10px;
    border: 1px solid #444;
    color: black;
    border-radius: 5px;
}

.index > a:hover {
    background-color: #3b5998;
    color: white;
}

.index .next,
.index .previous {
    padding-bottom: 6px;
}

.index.hidden {
    display: none;
}


/* scrollbar */
/* width */

/* Track */
::-webkit-scrollbar {
    background-color: transparent;
    width: 70px;
}

@media screen and (min-width: 645px) {
    ::-webkit-scrollbar {
        width: 10px;
    }
}

@media screen and (max-width: 380px) {
    ::-webkit-scrollbar {
        width: 10px;
    }
}

@media screen and (max-width: 330px) {
    ::-webkit-scrollbar {
        width: 10px;
    }
}

::-webkit-scrollbar-track {
    box-shadow: inset 0 0 5px white;
    border-radius: 10px;
    max-height: 50%;
    height: 50%;
}

/* Handle */
::-webkit-scrollbar-thumb {
    background: #ddc0ad;
    border-radius: 10px;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
    background: #b36257;
}

/* tool tips */
.tooltip-message {
}

.tooltip-output {
    z-index: 100;
    display: none;
    position: absolute;
    border: 1px solid #ddd;
    background-color: #444;
    color: white;
    padding: 5px;
    top: 100px;
    left: 50px;
}
